class XRHandTouchFaceDebugger {
    constructor() {
        this.touchCol = new Color(XRHandTouchDefaults.DBG_FACE_TOUCH_COL);
        this.unTouchCol = new Color(XRHandTouchDefaults.DBG_FACE_UNTOUCH_COL);
        this.touchThroughEnterCol = new Color(XRHandTouchDefaults.DBG_FACE_TOUCHTHROUGH_ENTER_COL);
        this.touchThroughExitCol = new Color(XRHandTouchDefaults.DBG_FACE_TOUCHTHROUGH_EXIT_COL);
        this.scratchCol = new Color(XRHandTouchDefaults.DBG_FACE_SCRATCH_COL);
    }
    initialize(colors) {
        if (colors) {
            for (const [key, value] of Object.entries(colors)) {
                value !== undefined ? (this[`${key}Col`] = new Color(value)) : null;
            }
        }
    }
    colorTouchedFace(intersected, message) {
        this.colorFace(intersected, this.touchCol, message);
    }
    colorTouchThroughEnter(intersected, message) {
        this.colorFace(intersected, this.touchThroughEnterCol, message);
    }
    colorTouchThroughExit(intersected, message) {
        this.colorFace(intersected, this.touchThroughExitCol, message);
    }
    colorFastTouchEnter(intersected, message) {
        this.colorFace(intersected, this.touchThroughEnterCol, message);
    }
    colorScratch(intersected, message) {
        this.colorFace(intersected, this.scratchCol, message);
    }
    colorUnTouch(intersected, message) {
        this.colorFace(intersected, this.unTouchCol, message);
    }
    colorFace(intersected, color, message) {
        message ? console.log("XRHandTouch : colorFace! " + message) : console.log("XRHandTouch : colorFace!");
        const geom = intersected.object.geometry;
        const colorAttr = geom.getAttribute("color");
        if (colorAttr !== undefined) {
            colorAttr.setXYZ(intersected.face.a, color.r, color.g, color.b);
            colorAttr.setXYZ(intersected.face.b, color.r, color.g, color.b);
            colorAttr.setXYZ(intersected.face.c, color.r, color.g, color.b);
            colorAttr.needsUpdate = true;
        }
    }
}

/**
 * @author Vatroslav Vrbanic @see https://github.com/vatro
 */
class XRHandTouchJointDebugger {
    constructor() {
        this.normalCol = XRHandTouchDefaults.DBG_JOINT_NORMAL_COL;
        this.touchCol = XRHandTouchDefaults.DBG_JOINT_TOUCH_COL;
        this.jointMat = new MeshBasicMaterial({
            color: this.normalCol
        });
    }
    initialize(currentScene, colors) {
        this.currentScene = currentScene;
        if (colors) {
            for (const [key, value] of Object.entries(colors)) {
                value !== undefined ? (this[`${key}Col`] = value) : null;
            }
        }
    }
    setJointMesh(handSpace, joint, i) {
        this.jointMesh = this.getJointMesh(handSpace, joint, i);
    }
    getJointMesh(handSpace, joint, i) {
        let jointMesh;
        if (handSpace.children[25].children.length > 0) {
            if (handSpace.children[25].children[0].children.length > 2) {
                jointMesh = handSpace.children[25].children[0].children[i];
                if (!joint.userData.hasDebugMaterial) {
                    joint.userData.hasDebugMaterial = true;
                    jointMesh.material = this.jointMat.clone();
                }
                return jointMesh;
            }
        }
        return undefined;
    }
    unhighlightJoint() {
        if (this.jointMesh?.material?.hasOwnProperty("color")) {
            const mat = this.jointMesh.material;
            mat.color.setHex(this.normalCol);
        }
    }
    highlightJoint() {
        if (this.jointMesh?.material?.hasOwnProperty("color")) {
            const mat = this.jointMesh.material;
            mat.color.setHex(this.touchCol);
        }
    }
}

